mapper.select:
SELECT pam.pottery_altar_number AS potteryAltarNumber, sdd.dict_label as potteryAltarState, tam.area_name AS areaName, fz.fire_zone_name AS fireZoneName, lm.liquor_name AS liquorName, lpm.liquor_batch_id AS liquorBatchId, lpm.actual_weight AS actualWeight, IFNULL(TIMESTAMPDIFF( DAY, lpm.storing_time, NOW()), 0) AS StorageDuration FROM laocz_batch_pottery_mapping lpm LEFT JOIN laocz_liquor_batch lb ON lpm.liquor_batch_id = lb.liquor_batch_id LEFT JOIN laocz_liquor_management lm ON lb.liquor_management_id = lm.liquor_management_id LEFT JOIN laocz_pottery_altar_management pam ON lpm.pottery_altar_id = pam.pottery_altar_id LEFT JOIN sys_dict_data sdd ON pam.pottery_altar_state = sdd.dict_value AND sdd.dict_type = 'Ceramide_condition' LEFT JOIN laocz_fire_zone_info fz ON pam.fire_zone_id = fz.fire_zone_id LEFT JOIN laocz_area_info tam ON fz.area_id = tam.area_id
fz.fire_zone_id = #{fireZoneId}
AND lpm.liquor_batch_id LIKE CONCAT('%', #{liquorBatchId}, '%')
AND pam.pottery_altar_number LIKE CONCAT('%', #{potteryAltarNumber}, '%')
AND lm.liquor_name LIKE CONCAT('%', #{liquorName}, '%')
AND tam.area_id = #{areaId}

SELECT pam.pottery_altar_number AS potteryAltarNumber, COALESCE(lm.liquor_name, '--') AS liquorName, COALESCE(lb.liquor_batch_id, '--') AS liquorBatchId, -- 假设liquor_batch_id为整数类型，用0替换NULL COALESCE(bpm.actual_weight, 0.0) AS actualWeight, -- 假设actual_weight为浮点数类型，用0.0替换NULL DATEDIFF(CURRENT_DATE, bpm.storing_time) AS retentionDays, COALESCE(lm.liquor_level, '--') AS liquorLevel, COALESCE(lm.liquor_round, '--') AS liquorRound, COALESCE(lm.liquor_flavor_name, '--') AS liquorFlavorName, COALESCE(lm.liquor_source, '--') AS liquorSource, COALESCE(lm.liquor_year, '--') AS liquorYear, COALESCE(lm.liquor_brewing_time, '0000-00-00') AS liquorBrewingTime, COALESCE(lm.liquor_content, '--') AS liquorContent FROM laocz_pottery_altar_management pam LEFT JOIN laocz_batch_pottery_mapping bpm ON bpm.pottery_altar_id = pam.pottery_altar_id AND bpm.real_status != 0 LEFT JOIN laocz_liquor_batch lb ON bpm.liquor_batch_id = lb.liquor_batch_id LEFT JOIN laocz_liquor_management lm ON lb.liquor_management_id = lm.liquor_management_id LEFT JOIN laocz_fire_zone_info fzi ON pam.fire_zone_id = fzi.fire_zone_id LEFT JOIN laocz_area_info ai ON fzi.area_id = ai.area_id
fzi.fire_zone_id = #{fireZoneId}
AND ai.area_id = #{areaId}
AND pam.pottery_altar_state = 1

SELECT lm.liquor_name, lb.liquor_batch_id, count(bpm.liquor_batch_id) as count FROM laocz_liquor_batch lb LEFT JOIN laocz_liquor_management lm ON lb.liquor_management_id = lm.liquor_management_id left join laocz_batch_pottery_mapping bpm on bpm.liquor_batch_id = lb.liquor_batch_id
bpm.liquor_batch_id = #{liquorBatchId}
GROUP BY bpm.liquor_batch_id
