mapper.select:
SELECT pam.pottery_altar_number AS potteryAltarNumber, sdd.dict_label as potteryAltarState, tam.area_name AS areaName, fz.fire_zone_name AS fireZoneName, lm.liquor_name AS liquorName, lpm.liquor_batch_id AS liquorBatchId, lpm.actual_weight AS actualWeight, TIMESTAMPDIFF( DAY, lpm.storing_time, NOW()) AS StorageDuration FROM laocz_batch_pottery_mapping lpm LEFT JOIN laocz_liquor_batch lb ON lpm.liquor_batch_id = lb.liquor_batch_id LEFT JOIN laocz_liquor_management lm ON lb.liquor_management_id = lm.liquor_management_id LEFT JOIN laocz_pottery_altar_management pam ON lpm.pottery_altar_id = pam.pottery_altar_id LEFT JOIN sys_dict_data sdd ON pam.pottery_altar_state = sdd.dict_value AND sdd.dict_type = 'Ceramide_condition' LEFT JOIN laocz_fire_zone_info fz ON pam.fire_zone_id = fz.fire_zone_id LEFT JOIN laocz_area_info tam ON fz.area_id = tam.area_id
fz.fire_zone_id = #{fireZoneId}
AND lpm.liquor_batch_id LIKE CONCAT('%', #{liquorBatchId}, '%')
AND pam.pottery_altar_number LIKE CONCAT('%', #{potteryAltarId}, '%')
AND lm.liquor_name LIKE CONCAT('%', #{liquorName}, '%')
AND tam.area_id = #{areaId}

SELECT pam.pottery_altar_number AS potteryAltarNumber, lm.liquor_name AS liquorName, lb.liquor_batch_id AS liquorBatchId, bpm.actual_weight AS actualWeight, DATEDIFF(CURRENT_DATE, bpm.storing_time) AS retentionDays, lm.liquor_level AS liquorLevel, lm.liquor_round AS liquorRound, lm.liquor_flavor_name AS liquorFlavorName, lm.liquor_source AS liquorSource, lm.liquor_year AS liquorYear, lm.liquor_brewing_time AS liquorBrewingTime, lm.liquor_content AS liquorContent FROM laocz_pottery_altar_management pam LEFT JOIN laocz_batch_pottery_mapping bpm ON bpm.pottery_altar_id = pam.pottery_altar_id LEFT JOIN laocz_liquor_batch lb ON bpm.liquor_batch_id = lb.liquor_batch_id LEFT JOIN laocz_liquor_management lm ON lb.liquor_management_id = lm.liquor_management_id LEFT JOIN laocz_fire_zone_info fzi ON pam.fire_zone_id = fzi.fire_zone_id LEFT JOIN laocz_area_info ai ON fzi.area_id = ai.area_id
fzi.fire_zone_id = #{fireZoneId}
AND ai.area_id = #{areaId}
