mapper.select:
SELECT lfzi.fire_zone_name, lai.area_name, lpam.pottery_altar_id, lpam.pottery_altar_number, lpam.pottery_altar_state, lpam.pottery_altar_full_altar_weight, lpam.pottery_altar_qr_code_address, lbpm.actual_weight, llb.liquor_management_id, lbpm.liquor_batch_id, lbpm.storing_time FROM laocz_pottery_altar_management AS lpam LEFT JOIN laocz_batch_pottery_mapping AS lbpm ON lpam.pottery_altar_id = lbpm.pottery_altar_id LEFT JOIN laocz_fire_zone_info AS lfzi ON lpam.fire_zone_id = lfzi.fire_zone_id LEFT JOIN laocz_area_info AS lai ON lai.area_id = lfzi.area_id LEFT JOIN laocz_liquor_batch AS llb ON lbpm.liquor_batch_id=llb.liquor_batch_id
lbpm.pottery_altar_id IS NOT NULL
lbpm.pottery_altar_id IS NULL
lbpm.pottery_altar_id IS NULL OR lbpm.liquor_batch_id=#{liquorBatchId}
AND NOT EXISTS ( SELECT 1 FROM laocz_batch_pottery_mapping lbpm2 WHERE lbpm2.pottery_altar_id = lpam.pottery_altar_id AND lbpm2.pottery_altar_id IN
#{id}
)
AND lpam.fire_zone_id = #{fireZoneId}
AND lpam.pottery_altar_number LIKE CONCAT('%', #{potteryAltarNumber}, '%')

select lpam.pottery_altar_id, lpam.pottery_altar_number, lpam.pottery_altar_state, lai.area_name, lfzi.area_id, lfzi.fire_zone_id, lfzi.fire_zone_name, lpam.pottery_altar_full_altar_weight, lpam.pottery_altar_qr_code_address FROM laocz_pottery_altar_management lpam LEFT JOIN laocz_fire_zone_info lfzi on lpam.fire_zone_id = lfzi.fire_zone_id LEFT JOIN laocz_area_info lai on lfzi.area_id = lai.area_id left join laocz_batch_pottery_mapping bpm on bpm.pottery_altar_id = lpam.pottery_altar_id
AND lfzi.area_id = #{areaId}
AND lpam.fire_zone_id = #{fireZoneId}
AND lpam.pottery_altar_number like concat('%', #{potteryAltarNumber}, '%')
AND bpm.liquor_batch_id like concat('%', #{liquorBatchId}, '%')
order by lpam.create_time desc

SELECT lfzi.fire_zone_name, lai.area_name, lpam.pottery_altar_id, lpam.pottery_altar_number, lpam.pottery_altar_full_altar_weight, llb.liquor_management_id FROM laocz_pottery_altar_management AS lpam LEFT JOIN laocz_batch_pottery_mapping AS lbpm ON lpam.pottery_altar_id = lbpm.pottery_altar_id LEFT JOIN laocz_fire_zone_info AS lfzi ON lpam.fire_zone_id = lfzi.fire_zone_id LEFT JOIN laocz_area_info AS lai ON lai.area_id = lfzi.area_id LEFT JOIN laocz_liquor_batch AS llb ON lbpm.liquor_batch_id=llb.liquor_batch_id
lbpm.pottery_altar_id IS NULL
AND lpam.fire_zone_id = #{fireZoneId}
AND lpam.pottery_altar_number LIKE CONCAT('%', #{potteryAltarNumber}, '%')
AND lpam.pottery_altar_state = #{potteryAltarState}

SELECT lfzi.fire_zone_name, lai.area_name, lpam.pottery_altar_id, lpam.pottery_altar_number, lpam.pottery_altar_full_altar_weight, llb.liquor_management_id, lbpm.actual_weight, lbpm.storing_time FROM laocz_pottery_altar_management AS lpam LEFT JOIN laocz_batch_pottery_mapping AS lbpm ON lpam.pottery_altar_id = lbpm.pottery_altar_id LEFT JOIN laocz_fire_zone_info AS lfzi ON lpam.fire_zone_id = lfzi.fire_zone_id LEFT JOIN laocz_area_info AS lai ON lai.area_id = lfzi.area_id LEFT JOIN laocz_liquor_batch AS llb ON lbpm.liquor_batch_id=llb.liquor_batch_id
lbpm.pottery_altar_id IS NOT NULL
AND lpam.fire_zone_id = #{fireZoneId}
AND lpam.pottery_altar_number LIKE CONCAT('%', #{potteryAltarNumber}, '%')
AND lpam.pottery_altar_state = #{potteryAltarState}
AND lbpm.real_status = #{realStatus}

SELECT lfzi.fire_zone_name, lai.area_name, lpam.pottery_altar_id, lpam.pottery_altar_number, lpam.pottery_altar_full_altar_weight, llb.liquor_management_id, lbpm.actual_weight FROM laocz_pottery_altar_management AS lpam LEFT JOIN laocz_batch_pottery_mapping AS lbpm ON lpam.pottery_altar_id = lbpm.pottery_altar_id LEFT JOIN laocz_fire_zone_info AS lfzi ON lpam.fire_zone_id = lfzi.fire_zone_id LEFT JOIN laocz_area_info AS lai ON lai.area_id = lfzi.area_id LEFT JOIN laocz_liquor_batch AS llb ON lbpm.liquor_batch_id=llb.liquor_batch_id
lbpm.pottery_altar_id IS NOT NULL
AND lpam.fire_zone_id = #{fireZoneId}
AND lpam.pottery_altar_number LIKE CONCAT('%', #{potteryAltarNumber}, '%')
AND lpam.pottery_altar_state = #{potteryAltarState}
AND lbpm.real_status = #{realStatus}
