mapper.select:
SELECT lfzi.fire_zone_name, lai.area_name, lpam.pottery_altar_id, lpam.pottery_altar_number, lpam.pottery_altar_state, lpam.pottery_altar_full_altar_weight, lpam.pottery_altar_qr_code_address FROM laocz_pottery_altar_management AS lpam LEFT JOIN laocz_batch_pottery_mapping AS lbpm ON lpam.pottery_altar_id = lbpm.pottery_altar_id LEFT JOIN laocz_fire_zone_info AS lfzi ON lpam.fire_zone_id = lfzi.fire_zone_id LEFT JOIN laocz_area_info AS lai ON lai.area_id = lfzi.area_id
lbpm.pottery_altar_id IS NULL
AND lpam.pottery_altar_id NOT IN
#{item}
AND lpam.fire_zone_id = #{fireZoneId}
AND lpam.pottery_altar_number like concat('%', #{potteryAltarNumber}, '%')
