SELECT lwd.wine_details_id, lwd.pottery_altar_apply_weight, lwd.liquor_batch_id, lwd.busy_status, lpam.pottery_altar_number, lpam.pottery_altar_full_altar_weight, lfzi.fire_zone_name, lai.area_name, llm.liquor_management_id, llm.liquor_name FROM laocz_wine_details lwd LEFT JOIN laocz_pottery_altar_management lpam ON lwd.pottery_altar_id = lpam.pottery_altar_id LEFT JOIN laocz_fire_zone_info lfzi ON lpam.fire_zone_id = lfzi.fire_zone_id LEFT JOIN laocz_area_info lai ON lai.area_id = lfzi.area_id LEFT JOIN laocz_liquor_batch llb ON lwd.liquor_batch_id = llb.liquor_batch_id LEFT JOIN laocz_liquor_management llm ON llb.liquor_management_id = llm.liquor_management_id
lwd.wine_details_id = #{wineDetailsId}
